mapper.select:
SELECT e.*, et.event_type_name FROM event e JOIN event_type et ON e.event_type_num = et.event_type_num
WHERE e.event_subject LIKE CONCAT('%', #{searchKeyword}, '%') OR et.event_type_name LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY event_reg_date desc LIMIT #{startRow} , #{listLimit}

mapper.insert:
INSERT INTO 
	coupon
VALUES (
	null
	, #{coupon_type_num}
	, #{id}
	, 1 -- coupon_status
)

mapper.select:
SELECT count(*) 
FROM coupon
WHERE 
	member_id = #{id}
AND 
	coupon_type_num = #{coupon_type_num}
